import UIKit

class DoingRouter: DoingWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = DoingViewController(nibName: nil, bundle: nil)
        let interactor = DoingInteractor()
        let router = DoingRouter()
        let presenter = DoingPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
}
